        static List<EmployeeTask> _largeData;
        static object _largeDataLock = new object();

        public List<EmployeeTask> GenerateLargeData() {
            if(_largeData == null) {
                lock(_largeDataLock) {
                    if(_largeData == null)
                        _largeData = GenerateLargeDataCore();
                }
            }
            return _largeData;
        }

        List<EmployeeTask> GenerateLargeDataCore() {
            var generatedCount = 100000;
            var itemsOnEachLevel = 5;
            var result = new List<EmployeeTask>();
            var data = GenerateData();

            var employeeNames = data.Select(x => x.EmployeeName).Distinct().ToArray();
            var taskNames = data.Select(x => x.Name).Distinct().ToArray();
            var startDates = data.Select(x => x.StartDate).Distinct().ToArray();
            var dueDates = data.Select(x => x.DueDate).Distinct().ToArray();

            void GenerateEmployeeTasks(List<EmployeeTask> data, int parentId, int level) {
                var id = result.Count + 1;

                if(level > 0) {
                    data.Add(new EmployeeTask(
                        id,
                        parentId,
                        taskNames[id % taskNames.Count()],
                        employeeNames[id % employeeNames.Count()],
                        startDates[id % startDates.Count()].ToString(),
                        dueDates[id % dueDates.Count()].ToString(),
                        id % 3 - 1,
                        id % 101,
                        string.Empty,
                        level > 1
                    ));

                    for(var i = 0; i < itemsOnEachLevel; i++) {
                        GenerateEmployeeTasks(data, id, level - 1);
                    }
                }
            }

            while(result.Count < generatedCount) {
                GenerateEmployeeTasks(result, 0, itemsOnEachLevel);
            }

            return result;
        }
